INSERT INTO erappsmus.competencias (id, tipo_competencia, codigo, descripcion) VALUES (1, 'Basica', 'CB1', 'Que los estudiantes hayan demostrado poseer y comprender conocimientos en un área de estudio que parte de la base de la educación secundaria general, y se suele encontrar a un nivel que, si bien se apoya en libros de texto avanzados, incluye también algunos aspectos que implican conocimientos procedentes de la vanguardia de su campo de estudio.');
INSERT INTO erappsmus.competencias (id, tipo_competencia, codigo, descripcion) VALUES (2, 'Basica', 'CB2', 'Que los estudiantes sepan aplicar sus conocimientos a su trabajo o vocación de una forma profesional y posean las competencias que suelen demostrarse mediante la elaboración y defensa de argumentos y la resolución de problemas dentro de su área de estudio');
INSERT INTO erappsmus.competencias (id, tipo_competencia, codigo, descripcion) VALUES (3, 'Basica', 'CB3', 'Que los estudiantes tengan la capacidad de reunir e interpretar datos relevantes (normalmente dentro de su área de estudio) para emitir juicios que incluyan una reflexión sobre temas relevantes de índole social, científica o ética');
INSERT INTO erappsmus.competencias (id, tipo_competencia, codigo, descripcion) VALUES (4, 'Basica', 'CB4', 'Que los estudiantes puedan transmitir información, ideas, problemas y soluciones a un público tanto especializado como no especializado');
INSERT INTO erappsmus.competencias (id, tipo_competencia, codigo, descripcion) VALUES (5, 'Basica', 'CB5', 'Que los estudiantes hayan desarrollado aquellas habilidades de aprendizaje necesarias para emprender estudios posteriores con un alto grado de autonomía');
INSERT INTO erappsmus.competencias (id, tipo_competencia, codigo, descripcion) VALUES (6, 'General', 'CG10', 'Conocimientos para la realización de mediciones, cálculos, valoraciones, tasaciones, peritaciones, estudios, informes, planificación de tareas y otros trabajos análogos de informática, de acuerdo con los conocimientos adquiridos según lo establecido en el apartado 5, anexo 2, de la resolución BOE-A');
INSERT INTO erappsmus.competencias (id, tipo_competencia, codigo, descripcion) VALUES (7, 'General', 'CG11', 'Capacidad para analizar y valorar el impacto social y medioambiental de las soluciones técnicas, comprendiendo la responsabilidad ética y profesional de la actividad del Ingeniero Técnico en Informática');
INSERT INTO erappsmus.competencias (id, tipo_competencia, codigo, descripcion) VALUES (8, 'General', 'CG3', 'Capacidad para diseñar, desarrollar, evaluar y asegurar la accesibilidad, ergonomía, usabilidad y seguridad de los sistemas, servicios y aplicaciones informáticas, así como de la información que gestionan');
INSERT INTO erappsmus.competencias (id, tipo_competencia, codigo, descripcion) VALUES (9, 'General', 'CG4', 'Capacidad para definir, evaluar y seleccionar plataformas hardware y software para el desarrollo y la ejecución de sistemas, servicios y aplicaciones informáticas, de acuerdo con los conocimientos adquiridos según lo establecido en el apartado 5, anexo 2, de la resolución BOE-A');
INSERT INTO erappsmus.competencias (id, tipo_competencia, codigo, descripcion) VALUES (10, 'General', 'CG5', 'Capacidad para concebir, desarrollar y mantener sistemas, servicios y aplicaciones informáticas empleando los métodos de la ingeniería del software como instrumento para el aseguramiento de su calidad, de acuerdo con los conocimientos adquiridos según lo establecido en el apartado 5, anexo 2, de la resolución BOE-A');
INSERT INTO erappsmus.competencias (id, tipo_competencia, codigo, descripcion) VALUES (11, 'General', 'CG6', 'Capacidad para concebir y desarrollar sistemas o arquitecturas informáticas centralizadas o distribuidas integrando hardware, software y redes de acuerdo con los conocimientos adquiridos según lo establecido en el apartado 5, anexo 2, de la resolución BOE-A');
INSERT INTO erappsmus.competencias (id, tipo_competencia, codigo, descripcion) VALUES (12, 'General', 'CG8', 'Conocimiento de las materias básicas y tecnologías, que capaciten para el aprendizaje y desarrollo de nuevos métodos y tecnologías, así como las que les doten de una gran versatilidad para adaptarse a nuevas situaciones');
INSERT INTO erappsmus.competencias (id, tipo_competencia, codigo, descripcion) VALUES (13, 'General', 'CG9', 'Capacidad para resolver problemas con iniciativa, toma de decisiones, autonomía y creatividad. Capacidad para saber comunicar y transmitir los conocimientos, habilidades y destrezas de la profesión de Ingeniero Técnico en Informática');
INSERT INTO erappsmus.competencias (id, tipo_competencia, codigo, descripcion) VALUES (14, 'Interna', 'CIB4', 'Conocimientos básicos sobre el uso y programación de los ordenadores, sistemas operativos, bases de datos y programas informáticos con aplicación en ingeniería');
INSERT INTO erappsmus.competencias (id, tipo_competencia, codigo, descripcion) VALUES (15, 'Interna', 'CIB5', 'Conocimiento de la estructura, organización, funcionamiento e interconexión de los sistemas informáticos, los fundamentos de su programación, y su aplicación para la resolución de problemas propios de la ingeniería.');
INSERT INTO erappsmus.competencias (id, tipo_competencia, codigo, descripcion) VALUES (16, 'Interna', 'CIC6', 'Capacidad para comprender, aplicar y gestionar la garantía y seguridad de los sistemas informáticos');
INSERT INTO erappsmus.competencias (id, tipo_competencia, codigo, descripcion) VALUES (17, 'Interna', 'CSI2', 'Capacidad para determinar los requisitos de los sistemas de información y comunicación de una organización atendiendo a aspectos de seguridad y cumplimiento de la normativa y la legislación vigente');
INSERT INTO erappsmus.competencias (id, tipo_competencia, codigo, descripcion) VALUES (18, 'Interna', 'CSI5', 'Capacidad para comprender y aplicar los principios de la evaluación de riesgos y aplicarlos correctamente en la elaboración y ejecución de planes de actuación');
INSERT INTO erappsmus.competencias (id, tipo_competencia, codigo, descripcion) VALUES (19, 'Resultado Aprend', 'RA1', 'Conocer los fundamentos de un sistema operativo, sus componentes y los conceptos esenciales para la comprensión de los mismos');
